import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { addNewCar, updateCar } from 'store/modules/listReducer';

function UseControlPanelForm() {
   const dispatch = useDispatch();
   const selectedCar = useSelector( store => store.list.selectedCar );
   const [ newCar, setNewCar ] = useState({ name: '', color: '#423d4d' });
   const [ updatedCar, setUpdatedCar ] = useState({ name: '', color: '#423d4d' });
   const [ updateBtnDisable, setUpdateBtnDisable ] = useState( true );

   useEffect(() => {
      console.log('selected car: ', selectedCar)
      if ( selectedCar ) setUpdatedCar({ name: selectedCar.name, color: selectedCar.color });
   }, [ selectedCar ])

   const submitNewCar = () => dispatch( addNewCar( newCar ) );
   const submitUpdatedCar = () => dispatch( updateCar( updateCar ) );

   const handleNewCar = ( newData ) => setNewCar({ ...newCar, ...newData });
   const handleUpdatedCar = ( newData ) => setUpdatedCar({ ...newCar, ...newData });

   useEffect(() => {
      if ( selectedCar && ( updatedCar.name !== selectedCar.name || updatedCar.color !== selectedCar.color ) ) {
         setUpdateBtnDisable( false );
      }
   }, [ updatedCar ])

   return{
      newCar,
      updatedCar,
      handleNewCar,
      handleUpdatedCar,
      submitNewCar,
      submitUpdatedCar,
      updateBtnDisable,
   }
}

export default UseControlPanelForm;
