import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

import { fetchGarageList, fetchWinnersList } from 'CONSTANTS/Axios';


const getWinnersList = createAsyncThunk(
  'list/getWinnersList',
  async (props, { rejectWithValue }) => {
    const data = await fetchWinnersList()

    if (data.isFailed) return rejectWithValue()
    return data   // this becomes action.payload
  }
)

const getGarageList = createAsyncThunk(
  'list/getGarageList',
  async (props, { rejectWithValue }) => {
    const data = await fetchWinnersList()

    if (data.isFailed) return rejectWithValue()
    return data   // this becomes action.payload
  }
)

const getGarageAndWinnersLists = createAsyncThunk(
   'list/getGarageAndWinners',
   async ( props, { rejectWithValue, dispatch }) => {
      const garageData = await fetchGarageList()
      if ( garageData.isFailed ) return rejectWithValue()

      const winnersData = await dispatch( getWinnersList() ).unwrap()
      if ( winnersData.isFailed ) return rejectWithValue()

      const enrichedWinners = winnersData.map(winner => {
         const car = garageData.find(current => current.id === winner.id)
         return {
         ...winner,
         name: car?.name ?? 'Unknown',
         color: car?.color ?? '#000',
         }
      })

      return {
         garageList: garageData,
         winnersList: enrichedWinners,
      }
   }
)


const initialState = {
   garageList: [],
   winnersList: [],
   loading: false,
};

const appSlice = createSlice({
   name: 'list',
   initialState,
   reducers: {
      setGarageList( state, { payload } ) {
         state.garageList = payload;
      },
   },
   extraReducers: ( builder ) => {
      builder
      .addCase( getGarageAndWinnersLists.pending, ( state ) => {
         state.loading = true;
      })
      .addCase( getGarageAndWinnersLists.fulfilled, ( state, { payload } ) => {
         state.garageList = payload.garageList;
         state.winnersList = payload.winnersList;
         state.loading = false;
      })
      .addCase( getGarageAndWinnersLists.rejected, ( state ) => {
         state.loading = false;
      })
   }
});

export const { 
} = appSlice.actions;
export { 
   getGarageAndWinnersLists,
};
export default appSlice.reducer;
