import React, { useState } from 'react';
import classNames from 'classnames';
import { useDispatch, useSelector } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faStop, faCheck, faTrash, faRefresh } from '@fortawesome/free-solid-svg-icons';

import { fetchCarDrive } from 'CONSTANTS/Axios';
import { deleteCar, setSelectedCar } from 'store/modules/listReducer';

import Button from 'components/commons/Button/Button';
import CarIcon from 'assets/icons/CarIcon/CarIcon';
import StartLight from '../StartLight/StartLight';

import styles from './GarageTableRow.module.scss';

function GarageTableRow({ carData }) {
   // const dispatch = useDispatch();
   const { id, name, color } = carData;
   // const selectedCar = useSelector( store => store.list.selectedCar );
   // const [ speed, setSpeed ] = useState( 0 );
   // const [ raceStatus, setRaceStatus ] = useState( 'stopped' );
   // const animationStyle = { animation: `${ styles.moveToEnd } ${ speed }s linear 4.2s forwards` };
   
   

   return(
      <div className = { styles.carTrack}>
         <h4 className = { styles.carName }>{ name }</h4>
         <StartLight raceState = { raceStatus == 'started' }/>
         <div className = { styles.controlBlock }>
            <Button 
               isDisable = { raceStatus != "stopped" }
               style = { styles.controlBlockBtn } 
               name = { <FontAwesomeIcon icon = { faPlay } color = "#28a745" /> }
               functionality = { () => { 
                  setRaceStatus( 'started' );
                  fetchCarDrive( id, 'started' )
                  .then( response => {
                     if ( response?.isFailed ) setRaceStatus( 'stopped' );
                     else {
                        const speedValue = response.distance / ( response.velocity * 1500 );
                        setSpeed( speedValue );
                        setTimeout( () => setRaceStatus( 'finished' ), ( speedValue + 4.2 ) * 1000 );
                     }
                  });
               }}
            />
            <Button 
               isDisable = { raceStatus == "stopped" }
               style = { styles.controlBlockBtn } 
               name = { <FontAwesomeIcon icon = { raceStatus !== "finished" ? faStop : faRefresh } color = "#ffc107" /> }
               functionality = { () => {
                  fetchCarDrive( id, 'stopped' );
                  setRaceStatus( 'stopped' );
               }}
            />
            <Button 
               style = { styles.controlBlockBtn } 
               name = { <FontAwesomeIcon icon = { faCheck } color = { selectedCar?.id == id ? "#48bfeeff" : "#ccc" } /> }
               functionality = { () => dispatch( setSelectedCar( selectedCar?.id == id ? undefined : carData ) ) }
            />
            <Button 
               isDisable = { raceStatus == "started" }
               style = { styles.controlBlockBtn } 
               name = { <FontAwesomeIcon icon = { faTrash } color = "#dc3545"/> }
               functionality = { () => dispatch( deleteCar( id ) ) }
            />
         </div>
         <CarIcon 
            className = { classNames( styles.car,
               { [ styles.racingAnimation ] : raceStatus == "started" },
               { [ styles.inTheEnd ] : raceStatus == "finished" },
            )}
            style = { raceStatus == "started" ? animationStyle : null }
            width = { 100 } 
            height = { 42 }  
            fill = { color } 
         />
      </div>
   )
}




export default GarageTableRow;
