const updateGarageStatusesList = ( action, props ) => {
  const data = JSON.parse( sessionStorage.getItem( 'garageListStatuses' ) || '{}');

  switch ( action ) {
      case 'delete':
         delete data[ props.id ];
         break;

      case 'update':
         if ( props.id != null && props.newValue != null ) {
         data[ props.id ] = props.newValue;
         }
         break;

      case 'add':
         if ( props.id != null && props.newValue != null ) {
         data[ props.id ] = props.newValue;
         }
         break;
      
      case 'getItem':
         return data[ props.id ];

      case 'get':
         return data;

      default:
         console.warn(`Unknown action: ${action}`);
  }

   if ( action != 'get' ) sessionStorage.setItem('garageListStatuses', JSON.stringify( data ));
   else return;
};


export {
   updateGarageStatusesList
};
