import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
   faArrowRight,
   faArrowLeft,
} from '@fortawesome/free-solid-svg-icons';

import Button from 'components/commons/Button/Button';

import styles from './GarageTable.module.scss';

function GarageTable() {

   return(
      <div className = { styles.garageTable }>

         <div className = { styles.track }>

         </div>
         <div className = { styles.tableFooter }>
            {/* <Button name = { <FontAwesomeIcon icon = { faArrowLeft } /> }/>
            <p>Page #13</p>
            <Button name = { <FontAwesomeIcon icon = { faArrowRight } /> }/> */}
            <GradientPagination />
         </div>
      </div>
   )
}

export default GarageTable;



function GradientPagination({ totalPages }) {
  const [currentPage, setCurrentPage] = useState(1);

  const handlePrev = () => setCurrentPage(prev => Math.max(prev - 1, 1));
  const handleNext = () => setCurrentPage(prev => Math.min(prev + 1, totalPages));
  const handlePageClick = (page) => setCurrentPage(page);

  const buttonStyle = {
    padding: '6px 12px',
    borderRadius: '8px',
    border: '2px solid transparent',
    cursor: 'pointer',
    background: '#fff',
    transition: 'all 0.2s ease',
  };

  const activeStyle = {
    ...buttonStyle,
    borderImageSlice: 1,
    borderWidth: '2px',
    borderImageSource: 'linear-gradient(90deg, rgb(72, 191, 238) 10%, #a001ea 90%)',
    fontWeight: 'bold',
  };

  return (
    <div style={{ display: 'flex', gap: '8px', alignItems: 'center' }}>
      <button onClick={handlePrev} disabled={currentPage === 1} style={buttonStyle}>
        Prev
      </button>

      {Array.from({ length: totalPages }, (_, i) => (
        <button
          key={i + 1}
          onClick={() => handlePageClick(i + 1)}
          style={currentPage === i + 1 ? activeStyle : buttonStyle}
        >
          {i + 1}
        </button>
      ))}

      <button onClick={handleNext} disabled={currentPage === totalPages} style={buttonStyle}>
        Next
      </button>
    </div>
  );
}
