import React, { FC, ChangeEvent, FormEvent } from 'react';

import ControlPanelFormRow from 'components/commons/ControlPanelFormRow/ControlPanelFormRow';
import UseControlPanelForm from './UseControlPanelForm';

import styles from './ControlPanelForm.module.scss';

function ControlPanelForm() {
  const {
    newCar,
    updatedCar,
    handleNewCar,
    handleUpdatedCar,
    submitNewCar,
    submitUpdatedCar,
    updateBtnDisable,
  } = UseControlPanelForm();

  const renderRow = (
    namePlaceholder: string,
    car: typeof newCar | typeof updatedCar,
    handleCar: typeof handleNewCar | typeof handleUpdatedCar,
    handleSubmit: () => void,
    btnName: string,
    isDisable: boolean
  ) => (
    <ControlPanelFormRow
      namePlaceholder={namePlaceholder}
      name={car?.name}
      color={car?.color}
      handleName={e => handleCar({ name: e.target.value })}
      handleColor={e => handleCar({ color: e.target.value })}
      handleSubmit={handleSubmit}
      btnName={btnName}
      isDisable={isDisable}
    />
  );

  return (
    <form className={styles.form}>
      {renderRow('Create Car', newCar, handleNewCar, submitNewCar, 'Create', !newCar.name)}
      {renderRow('Select Car', updatedCar, handleUpdatedCar, submitUpdatedCar, 'Update', updateBtnDisable)}
    </form>
  );
}

export default ControlPanelForm;
