const updateGarageStatusesList = (action, id, newValue = null) => {
  const data = JSON.parse( sessionStorage.getItem( 'garageListStatuses' ) || '{}');

  switch ( action ) {
    case 'delete':
      delete data[ id ];
      break;

    case 'update':
      if ( id != null && newValue != null ) {
        data[ id ] = newValue;
      }
      break;

    case 'add':
      if ( id != null && newValue != null ) {
        data[ id ] = newValue;
      }
      break;

    default:
      console.warn(`Unknown action: ${action}`);
  }

  sessionStorage.setItem('garageListStatuses', JSON.stringify( data ));
};


export default updateGarageStatusesList;
