import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faStop, faCheck, faTrash, faCarSide } from '@fortawesome/free-solid-svg-icons';


import Button from 'components/commons/Button/Button';

import styles from './CarTrack.module.scss';

function CarTrack() {

   return(
      <div className = { styles.carTrack}>
         <div className = { styles.controlBlock }>
            <Button style = { styles.controlBlockBtn } name = { <FontAwesomeIcon icon = { faPlay } color = "#28a745" /> }/>
            <Button style = { styles.controlBlockBtn } name = { <FontAwesomeIcon icon = { faCheck } color = "#48bfeeff" /> }/>
            <Button style = { styles.controlBlockBtn } name = { <FontAwesomeIcon icon = { faStop } color = "#ffc107" /> }/>
            <Button style = { styles.controlBlockBtn } name = { <FontAwesomeIcon icon = { faTrash } color = "#dc3545"/> }/>
         </div>
         <CarIcon />
      </div>
   )
}
const CarIcon = ({ width = 64, height = 64, color = "#000" }) => (
     <svg
    xmlns="http://www.w3.org/2000/svg"
    viewBox="0 0 640 320"
    width={width}
    height={height}
  >
    {/* Car Body */}
    <path
      d="M60 200 L80 150 Q120 120 200 120 H440 Q500 120 540 150 L560 200 Z"
      fill={bodyColor}
      stroke="#000"
      strokeWidth="2"
    />
    {/* Windows */}
    <path
      d="M130 150 Q180 130 320 130 Q460 130 510 150 L510 190 L130 190 Z"
      fill="#fff"
      stroke="#000"
      strokeWidth="1.5"
    />
    {/* Wheels */}
    <circle cx="180" cy="210" r="30" fill={wheelColor} stroke="#000" strokeWidth="2" />
    <circle cx="480" cy="210" r="30" fill={wheelColor} stroke="#000" strokeWidth="2" />
    {/* Wheel rims */}
    <circle cx="180" cy="210" r="12" fill="#aaa" />
    <circle cx="480" cy="210" r="12" fill="#aaa" />
    {/* Headlight */}
    <ellipse cx="560" cy="180" rx="12" ry="6" fill="#ff0" />
    {/* Taillight */}
    <ellipse cx="60" cy="180" rx="12" ry="6" fill="#f00" />
    {/* Door line */}
    <line x1="260" y1="150" x2="260" y2="190" stroke="#000" strokeWidth="1" />
    <line x1="380" y1="150" x2="380" y2="190" stroke="#000" strokeWidth="1" />
    {/* Side mirror */}
    <rect x="520" y="150" width="12" height="6" fill={bodyColor} stroke="#000" strokeWidth="1" />
  </svg>

);



export default CarTrack;
