import React, { FC } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faPlay,
  faStop,
  faTriangleExclamation,
  faTrash,
  faRefresh,
  faSliders,
} from '@fortawesome/free-solid-svg-icons';

import Button from 'components/commons/Button/Button';
import styles from './GarageTableRow.module.scss';

interface GarageTableRowCntrolBlockProps {
  raceStatus: string;
  selectedCarId?: number;
  carId: number;
  handleStart: () => void;
  handleStop: () => void;
  toggleSelect: () => void;
  handleDelete: () => void;
}

function GarageTableRowCntrolBlock({ raceStatus, selectedCarId, carId, 
   handleStart, handleStop, toggleSelect, handleDelete
}: GarageTableRowCntrolBlockProps ){
   return (
   <div className={styles.controlBlock}>
      <Button
         isDisable={raceStatus !== 'stopped'}
         style={styles.controlBlockBtn}
         name={<FontAwesomeIcon icon={faPlay} color="#28a745" />}
         functionality={handleStart}
      />
      <Button
         isDisable={raceStatus === 'stopped'}
         style={styles.controlBlockBtn}
         name={
         <FontAwesomeIcon
            icon={raceStatus === 'finished' || raceStatus === 'brokenEngine' ? faRefresh : faStop}
            color="#ffc107"
         />
         }
         functionality={handleStop}
      />
      <Button
         isDisable={raceStatus === 'started'}
         style={styles.controlBlockBtn}
         name={<FontAwesomeIcon icon={faSliders} color={selectedCarId === carId ? '#48bfeeff' : '#ccc'} />}
         functionality={toggleSelect}
      />
      <Button
         isDisable={raceStatus === 'started'}
         style={styles.controlBlockBtn}
         name={<FontAwesomeIcon icon={faTrash} color="#dc3545" />}
         functionality={handleDelete}
      />
  </div>
)};

export default GarageTableRowCntrolBlock;
