import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { useAppSelector, useAppDispatch } from 'store/hooks/hooks';

import { setWinner } from 'store/modules/listReducer';
import { faTrophy } from '@fortawesome/free-solid-svg-icons';

import styles from './WinnerBanner.module.scss';

function WinnerBanner() {
   const dispatch = useAppDispatch();
   const winner = useAppSelector( store => store.list.winner );

   if( !winner ) return null;
   return (
      <div className = { styles.winnerBanner }
         tabIndex={0}
         onClick = {() => dispatch(setWinner(undefined))}
      >
         <h4 className={styles.title}>
            <FontAwesomeIcon
            size="1x"
            color="rgb(72, 191, 238)"
            icon={faTrophy}
            />
            &nbsp;{`The ${winner.name} is a new Winner!`}&nbsp;
            <FontAwesomeIcon 
               size="1x" 
               color="#a001ea" 
               icon={faTrophy}
            />
         </h4>
      </div>
   );
}

export default WinnerBanner;
