import React, { FC, ChangeEvent, FormEvent } from 'react';

import ControlPanelFormRow from 'components/commons/ControlPanelFormRow/ControlPanelFormRow';
import UseControlPanelForm from './UseControlPanelForm';

import styles from './ControlPanelForm.module.scss';

function ControlPanelForm() {
   const {
      newCar,
      updatedCar,
      handleNewCar,
      handleUpdatedCar,
      submitNewCar,
      submitUpdatedCar,
      updateBtnDisable,
   } = UseControlPanelForm();

   const rows = [
      {
         namePlaceholder: 'Create Car',
         data: newCar,
         handle: handleNewCar,
         handleSubmit: submitNewCar,
         btnName: 'Create',
         isDisable: !newCar.name,
      },
      {
         namePlaceholder: 'Select Car',
         data: updatedCar,
         handle: handleUpdatedCar,
         handleSubmit: submitUpdatedCar,
         btnName: 'Update',
         isDisable: updateBtnDisable,
      },
   ];

   return (
      <form className={styles.form}>
         {rows.map((row, i) => (
            <ControlPanelFormRow
               key={i}
               namePlaceholder={row.namePlaceholder}
               name={row.data?.name}
               color={row.data?.color}
               handleName={(e: ChangeEvent<HTMLInputElement>) =>
                  row.handle({ name: e.target.value })
               }
               handleColor={(e: ChangeEvent<HTMLInputElement>) =>
                  row.handle({ color: e.target.value })
               }
               handleSubmit={row.handleSubmit}
               btnName={row.btnName}
               isDisable={row.isDisable}
            />
         ))}
      </form>
   );
}

export default ControlPanelForm;
