import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { addNewCar, updateCar } from 'store/modules/listReducer';

function ControlPanelForm() {
   const dispatch = useDispatch();
   const selectedCar = useSelector( store => store.list.selectedCar );
   const [ newCar, setNewCar ] = useState({ name: '', color: '#423d4d' });
   const [ updateCar, setUpdateCar ] = useState({ name: '', color: '#423d4d' });
   const [ createBtnDisable, setCreateBtnDisable ] = useState( true );
   const [ updateBtnDisable, setUpdateBtnDisable ] = useState( true );

   useEffect(() => {
      setUpdateCar({ name: selectedCar.name, color: selectedCar.color });
   }, [ selectedCar ])

   const submitNewCar = dispatch( addNewCar( newCar ) );
   const submitUpdatedCar = dispatch( updateCar( updateCar ) );

   return(
      setNewCar,
      setUpdateCar
   )
}

export default UseControlPanelForm;







