import React, { CSSProperties, FC, useEffect, useRef, useState } from 'react';
import { useAppDispatch } from 'store/hooks/hooks';

import { handleTablesPagesHistory, handleGarageRaceStatusesHistory } from 'Storages/SessionStorage';
import { Car, setRace } from 'store/modules/listReducer';

import FinishLine from 'components/commons/FinishLine/FinishLine';
import GarageTableRow from 'components/commons/GarageTableRow/GarageTableRow';
import Pagination from 'components/commons/Pagination/Pagination';

import styles from './GarageTable.module.scss';

interface GarageTableProps {
   list: Car[];
}

function GarageTable({ list }: GarageTableProps) {
   const dispatch = useAppDispatch();
   const pageInHistory = handleTablesPagesHistory('getItem', {
      key: 'garageTable',
   });
   const visibleitemsCount = 7;
   const maxPageCount = Math.ceil(list.length / visibleitemsCount);
   const [pageNo, setPageNo] = useState<number>(
      pageInHistory <= maxPageCount ? pageInHistory : maxPageCount
   );
   const tableRef = useRef<HTMLDivElement>(null);
   const [startLineStyle, setStartLineStyle] = useState<CSSProperties>({});
   const handlePageChange = (No: number) => setPageNo(No);

   useEffect(() => {
      handleTablesPagesHistory('update', {
         key: 'garageTable',
         newValue: pageNo,
      });
      dispatch(setRace(false));
      let areAnyRace = false;
      for (let index = 0; index < 7; index++) {
         const status: string = handleGarageRaceStatusesHistory('getItem', { key: id })
         if ( status !== 'stopped' ) dispatch( setRace('finished') );
      }
   }, [pageNo]);

   useEffect(() => {
      const tableHeight: number = tableRef?.current?.offsetHeight ?? 50;
      setStartLineStyle({
         fontSize: `${Math.min((tableHeight / 100) * 10, 24)}px`,
         letterSpacing: `${Math.min((tableHeight / 100) * 2, 10)}px`,
      });
   }, []);

   return (
      <div className={styles.garageTable} ref={tableRef}>
         <div className={styles.track}>
            <span className={styles.startLine} style={startLineStyle}>
               START
            </span>
            <FinishLine />
            {list
               .slice(
                  (pageNo - 1) * visibleitemsCount,
                  pageNo * visibleitemsCount
               )
               .map((car) => (
                  <GarageTableRow key={car.id} carData={car} />
               ))}
         </div>
         {list.length > visibleitemsCount ? (
            <Pagination
               count={list.length}
               pageNo={pageNo}
               changePage={(No) => handlePageChange(No)}
               visibleitemsCount={visibleitemsCount}
            />
         ) : null}
      </div>
   );
}

export default GarageTable;
