import React, { useState, useEffect } from 'react';

import Button from '../Button/Button';

import styles from './ControlPanelFormRow.module.scss';

function ControlPanelFormRow({ namePlaceholder, name, color, handleName, handleColor, handleSubmit, BtnName }) {
   // const [ color, setColor ] = useState( '#423d4d' );

   return(
      <div className = { styles.row }>
         <input 
            placeholder = { namePlaceholder } 
            value = { name }
            onChange = { handleName }
            className = { styles.nameInput }
         />
         <input 
            type = "color" 
            value = { selectedCar ? selectedCar.color : color } 
            onChange = { event  => setColor( event.target.value ) }
            className = { styles.colorBox }
         />
         <Button 
            name = { btnName } 
            isDisable = { !name } 
            functionality = { event => {
               event.preventDefault();
               setName( '' );
               setColor( '#423d4d' );
               return handleSubmit({ name, color });
            } }
         />
      </div>
   )
}

export default ControlPanelFormRow;
