import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

import { fetchGarageList, fetchWinnersList } from 'CONSTANTS/Axios';


const getGarageList = createAsyncThunk( 
   'list/getGarageList',
   async ( props, { rejectWithValue, dispatch } ) => {
      const garageList = await fetchGarageList( );

      if ( garageList.isFailed ) return rejectWithValue();

      const winnersData = await dispatch( getWinnersList() ).unwrap()
      if ( winnersData.isFailed ) return rejectWithValue();

      const resultList = garageData.map(car => {
         const winner = winnersData.find(w => w.id === car.id)
         return {
            ...car,
            wins: winner?.wins ?? 0,
            time: winner?.time ?? null,
         }
      })

      return data;
   }
); 

const getWinnersList = createAsyncThunk( 
   'list/getWinnersList',
   async ( props, { rejectWithValue } ) => {
      const data = await fetchWinnersList( );

      if ( data.isFailed ) return rejectWithValue();

      return data;
   }
); 

const initialState = {
   garageList: [],
   winnersList: [],
   loading: false,
};

const appSlice = createSlice({
   name: 'list',
   initialState,
   reducers: {
      setGarageList( state, { payload } ) {
         state.garageList = payload;
      },
   },
   extraReducers: ( builder ) => {
      builder
      .addCase( getGarageList.pending, ( state ) => {
         state.loading = true;
      })
      .addCase( getGarageList.fulfilled, ( state, { payload } ) => {
         state.garageList = payload;
         // state.loading = false;
      })
      .addCase( getGarageList.rejected, ( state ) => {
         // state.loading = false;
      })
      .addCase( getWinnersList.pending, ( state ) => {
         state.loading = true;
      })
      .addCase( getWinnersList.fulfilled, ( state, { payload } ) => {
         state.garageList = payload;
         state.loading = false;
      })
      .addCase( getWinnersList.rejected, ( state ) => {
         state.loading = false;
      })
   }
});

export const { 
   setGarageList,
} = appSlice.actions;
export { 
   getGarageList,
   getWinnersList,
};
export default appSlice.reducer;
