import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { useAppSelector } from 'store/hooks/hooks';

import { faTrophy } from '@fortawesome/free-solid-svg-icons';
import WinnersTable from 'components/featues/WinnersTable/WinnersTable';

import styles from './WinnerBanner.module.scss';

function WinnerBanner() {
   const winner = useAppSelector( store => store.list.winner );

   if( !winner ) return null;
   return (
      <div className = { styles.winnerBanner }>
            <h4 className={styles.title}>
               <FontAwesomeIcon
               size="1x"
               color="rgb(72, 191, 238)"
               icon={faTrophy}
               />
               &nbsp;{`The ${ winner.name } crossed the finish line first!`}&nbsp;
               <FontAwesomeIcon 
                  size="1x" 
                  color="#a001ea" 
                  icon={faTrophy}
               />
            </h4>
         <span>{`${winner.name}`}</span>
      </div>
   );
}

export default WinnerBanner;
