import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { useAppSelector } from 'store/hooks/hooks';

import styles from './WinnerBanner.module.scss';

function WinnerBanner() {
   const winner = useAppSelector( store => store.list.winner );
   return (
      <div className = { styles.winnerBanner }>
            <FontAwesomeIcon
               size="2x"
               color="rgb(72, 191, 238)"
               icon={faTrophy}
            />
            <h4 className={styles.title}>&nbsp;Winner&nbsp;</h4>
            <FontAwesomeIcon size="2x" color="#a001ea" icon={faTrophy}/>
      </div>
   );
}

export default WinnerBanner;
