import React, { useState } from 'react';

import Button from '../Button/Button';

import styles from './ControlPanelForm.module.scss';

function ControlPanelForm() {
   const [ newCar, setNewCar ] = useState({ name: '', color: '#423d4d' })
   const [ updatedCar, setUpdatedCar ] = useState({ name: '', color: '#423d4d' })

   return(
      <form className = { styles.form }>
         <div className = { styles.row }>
            <input 
               placeholder = "Create Car" 
               value = { newCar.name }
               onChange = { event  => setNewCar({ ...newCar, name: event.target.value })}
               className = { styles.nameInput }
            />
            <input 
               type = "color" 
               value = { newCar.color } 
               onChange = { event  => setNewCar({ ...newCar, color: event.target.value })}
               className = { styles.colorBox }
            />
            <Button name = "Create" />
         </div>
         <div className = { styles.row }>
            <input 
               placeholder = "Update Car" 
               value = { updatedCar.name }
               onChange = { event  => setNewCar({ ...updatedCar, name: event.target.value })}
               className = { styles.nameInput }
            />
            <input 
               type = "color" 
               value = { updatedCar.color }
               onChange = { event  => setNewCar({ ...updatedCar, name: event.target.value })}
               className = { styles.colorBox }
            />
            <Button name = "Update" />
         </div>
      </form>
   )
}

export default ControlPanelForm;
