import React, { FC, useState } from 'react';
import classNames from 'classnames';
import { useAppDispatch, useAppSelector } from 'store/hooks/hooks';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faStop, faTriangleExclamation, faTrash, faRefresh, faSliders } from '@fortawesome/free-solid-svg-icons';

import Button from 'components/commons/Button/Button';
import CarIcon from 'assets/icons/CarIcon/CarIcon';
import StartLight from '../StartLight/StartLight';

import UseGarageTableRow from './UseGarageTableRow';

import styles from './GarageTableRow.module.scss';

interface GarageTableRowProps {
   carData: {
      id: number;
      name: string;
      color: string;
   };
}

function GarageTableRow({ carData }: GarageTableRowProps) {
   const { id, name, color } = carData;
   const selectedCar = useAppSelector((store) => store.list.selectedCar);
   const { handleStart, handleStop, toggleSelect, handleDelete, raceStatus, animationStyle, handleFinish } = UseGarageTableRow(carData);

   return (
      <div className={styles.carTrack}>
         <h4 className={styles.carName}>
            {raceStatus == 'brokenEngine' ? <FontAwesomeIcon icon={faTriangleExclamation} color="#dc3545" /> : null}
            {` ${name}`}
         </h4>
         <StartLight raceState={raceStatus === 'started'} />
         <div className={styles.controlBlock}>
            {[
               { icon: faPlay, color: '#28a745', onClick: handleStart, disabled: raceStatus !== 'stopped' },
               { icon: raceStatus === 'finished' || raceStatus === 'brokenEngine' ? faRefresh : faStop, color: '#ffc107', onClick: handleStop, disabled: raceStatus === 'stopped' },
               { icon: faSliders, color: selectedCar?.id === id ? '#48bfeeff' : '#ccc', onClick: toggleSelect, disabled: raceStatus === 'started' },
               { icon: faTrash, color: '#dc3545', onClick: handleDelete, disabled: raceStatus === 'started' },
            ].map((btn, i) => (
               <Button key={i} isDisable={btn.disabled} style={styles.controlBlockBtn} name={<FontAwesomeIcon icon={btn.icon} color={btn.color} />} functionality={btn.onClick} />
            ))}
         </div>
         <CarIcon
            handleAnimationEnd={handleFinish}
            className={classNames(styles.car, { [styles.racingAnimation]: raceStatus == 'started' }, { [styles.inTheEnd]: raceStatus == 'finished' })}
            style={raceStatus == 'started' || raceStatus == 'brokenEngine' ? animationStyle : undefined}
            width={100}
            height={42}
            fill={color}
         />
      </div>
   );
}

export default GarageTableRow;
