import React, { useEffect, useState } from 'react';
import classNames from 'classnames';

import styles from './StartLight.module.scss';

function StartLight() {
   const [ lightPosition, setLightPosition ] = useState( 0 );

   const startAnimation = () => {
      let timer;
      if (activeLight < 4) {
         timer = setTimeout(() => {
         setLightPosition((prev) => prev + 1);
         }, 1000);
      }
      return () => clearTimeout(timer);
   };

   useEffect(() => {
      startAnimation();
   }, []);

   return (
      <div className = { styles.startLight }>
         <div className = { classNames( styles.light, {
            [ styles.red ] : lightPosition >= 1 && lightPosition < 4,
            [ styles.green ] : lightPosition == 4,
         }) }/>
         <div className = { classNames( styles.light, {
            [ styles.red ] : lightPosition >= 1 && lightPosition < 4,
            [ styles.green ] : lightPosition == 4,
         }) }/>
