import React from 'react';
import { useAppDispatch } from 'store/hooks/hooks';
import { setWinnersSortBy, setWinnersSortDirection } from 'store/modules/listReducer';
import ToggleButton from '../ToggleButton/ToggleButton';
import styles from './WinnerTableHead.module.scss';

const WinnerTableHead: React.FC = () => {
  const dispatch = useAppDispatch();

  const handleSortBy = (newValue: string | null) => {
    if (newValue !== null) void dispatch(setWinnersSortBy(newValue));
  };

  const handleSortDirection = (newValue: string | null) => {
    if (newValue !== null) void dispatch(setWinnersSortDirection(newValue));
  };

  return (
    <div className={styles.winnerTableHead}>
      <ToggleButton
        name="sortBy"
        valueOne="Wins"
        valueTwo="Time"
        startValue={null}
        onToggle={handleSortBy}
        uncheckedValue={null}
      />

      <ToggleButton
        name="sortDirection"
        valueOne="▲ Ascending"
        valueTwo="▼ Descending"
        startValue={null}
        onToggle={handleSortDirection}
        uncheckedValue={null}
      />
    </div>
  );
};

export default WinnerTableHead;
