import React, { FC, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { useAppSelector } from 'store/hooks/hooks';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight, faArrowLeft } from '@fortawesome/free-solid-svg-icons';

import Button from 'components/commons/Button/Button';

import styles from './Pagination.module.scss';

interface PaginationProps {
   count: number;
   pageNo: number;
   changePage: (page: number) => void;
   visibleitemsCount: number;
}

function Pagination({ count, pageNo, changePage, visibleitemsCount }: PaginationProps) {
   const { pathname } = useLocation();
   const race = useAppSelector((store) => store.list.race);
   console.log(pageNo == 1 || false)
   return (
      <div className={styles.pagination}>
         <Button
            name={
               <span>
                  <FontAwesomeIcon icon={faArrowLeft} /> Previous Page
               </span>
            }
            isDisable={ pageNo === 1 || false }
            functionality={() => changePage(pageNo - 1)}
         />
         <p>{`Page No.${pageNo}`}</p>
         <Button
            name={
               <span>
                  Next Page <FontAwesomeIcon icon={faArrowRight} />
               </span>
            }
            isDisable={
               pageNo == Math.ceil(count / visibleitemsCount) ||
               pathname == '/garage'
                  ? race == 'started'
                  : false
            }
            functionality={() => changePage(pageNo + 1)}
         />
      </div>
   );
}

export default Pagination;
