import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

import { fetchGarageList } from 'CONSTANTS/Axios';


const getGarageList = createAsyncThunk(
   'list/getGarageList',
   async ( { }, { rejectWithValue, dispatch } ) => {
      const data = await fetchGarageList( );

      if ( data.isFailed ) return rejectWithValue();

      return dispatch( setGarageList( data ) );
   }
); 

// const updateCar = createAsyncThunk(
//    'list/updateCar',
//    async ( { user, updatedCarData }, { rejectWithValue, dispatch } ) => {
//       const data = await fetchUpdateCar( user, updatedCarData );

//       if ( data.isFailed ) return rejectWithValue();

//       return dispatch( getPosts( user ) );
//    }
// );

const initialState = {
   garageList: [],
   winnersList: [],
   loading: false,
};

const appSlice = createSlice({
   name: 'list',
   initialState,
   reducers: {
      setGarageList( state, { payload } ) {
         state.garageList = payload;
      },
   },
   extraReducers: ( builder ) => {
      builder
      .addCase( getGarageList.pending, ( state ) => {
         state.loading = true;
         state.isAddCarPopupOpen = false;
      })
      .addCase( getGarageList.fulfilled, ( state ) => {
         state.loading = false;
      })
      .addCase( getGarageList.rejected, ( state ) => {
         state.loading = false;
      })
   }
});

export const { 
   setGarageList,
} = appSlice.actions;
export { getGarageList };
export default appSlice.reducer;
